package com.servlet.controller;

import java.io.IOException;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.Enumeration;
import java.util.Map;

import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
//어노테이션 방식으로 servletㅇ르 등록할 수 있다.
//어노테이션 : 클래스 선언부에 @WebServlet 어노테이션을 선언한다.
//@WebServlet어노테이션의 속성설정으로 urlpatten, name을 설정할 수 있다.
@WebServlet(name="paramdata",urlPatterns= {"/testperson.do"})
public class ParameterDataServlet extends HttpServlet{

	private static final long serialVersionUID = -7348993035964273633L;
	
	public ParameterDataServlet() {

	}

	@Override
	protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
		// 클라이언트가 보낸 데이터 확인하기
		//1. 단일데이터 가져오기
		//HttpServletRequest.getParameter("key")메소드를 이용한다.
		String name=req.getParameter("name");		
		int age=Integer.parseInt(req.getParameter("age"));
		double height=Double.parseDouble(req.getParameter("height"));
		String color=req.getParameter("color");
		String animals=req.getParameter("animal");
		//클라이언트가 동일한 key로 다수의 값을 보낸 데이터는 HttpServletRequest.getParameterValues()메소드를 이용한다.
		String[] animals2=req.getParameterValues("animal");
		String lunch=req.getParameter("lunch");
		String info=req.getParameter("info");
		
		System.out.println("이름 : "+name);
		System.out.println("나이 : "+age);
		System.out.println("키 : "+height);
		System.out.println("색상 : "+color);
		System.out.println("동물 : "+animals);
		System.out.print("동물들 : ");
		Arrays.asList(animals2).stream().forEach(System.out::print);
		System.out.println();
		System.out.println("점심 : "+lunch);
		System.out.println("소개 : "+info);
		
		//클라이언트가 보낸 데이터의 key를 모를 때
		//전송된 key값을 가져오는 방법 : HttpServletRquest.getParameterNames()메소드이용.
		Enumeration<String> paramName=req.getParameterNames();
		while(paramName.hasMoreElements()) {
			String key=paramName.nextElement();
			System.out.println(key);
			String[] value=req.getParameterValues(key);
			System.out.println(Arrays.toString(value));
		}
		//클라이언트가 보낸 데이터 Map방식으로 가져오기
		Map<String,String[]> param=req.getParameterMap();
		for(String key:param.keySet()) {
			System.out.println(key+" : "+Arrays.toString(param.get(key)));
		}

		//클라이언트 응답데이터 작성하기
        //HttpServletResponse객체가 제공하는 메소드를 이용한다
        //1. 응답데이터를 작성하기 위해 contentType을 설정
        // MIMETYPE설정
        // setContentType("MIMETYPE설정")
        resp.setContentType("text/html;charset=utf-8");//대소문자 구분x
        //2.응답데이터 보내기
        // 1)문자열 데이터 : 문자열 스트림으로 전송 -> getWriter();
        // 2)바이너리 데이터 : 파일 스트림으로 전송 -> getOutputStream();
        //#인터페이스
        PrintWriter out=resp.getWriter();//import
        //3. 원하는 데이터 전송하기
        //out.write("<h3>내가 만든 첫 응답페이지</h3>");
        String html="<html>";
        html+="<head>";
        html+="<title>개인취향테스트</title>";
        html+="</html>";
        html+="<body>";
        html+="<h3>개인취향결과</h3>";
        html+="<h4>"+name+"님의 개인취향 확인결과";
        html+="<h4>당신의 이름은 "+name+"이고 나이는 "+age+"살 이고,";
        html+="키는 "+height+"cm입니다.";
        html+="<h4>좋아하는 색은 <span style='color:"+color+"'>"+color+"</span>";
        html+="입니다";
        html+="<ul>좋아하는 동물은";
        for(String animal:animals2) {
        	String src="";
        	switch(animal) {
        	case "강아지":src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxWwZabQxrCn8EDSekGue9RRDmEYUnO7NOjQ&usqp=CAU";break;
        	case "고양이":src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgPe0X60U_s8fvws6YSbjxZCk6MlJCEBUqgg&usqp=CAU";break;
        	case "팽귄":src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTKIWtduQwn8jNeA2HiPOGmBklYzyn2-6-vRQ&usqp=CAU";break;
        	case "기린":src="data:image/jpeg;base64,";break;
        	case "토끼":src="data:image/jpeg;base64,";break;
        	case "코끼리":src="data:image/jpeg;base64,";break;
        	}
           html+="<li>"+"<img src='"+src+" width='200px' height='200px'>"+"</li>";
        }
        html+="입니다</ul>";
        html+="<p>오늘의 점심은 "+lunch+"입니다.</p>";
        html+="<h3>소개란에 적은 내용은 "+info+"</h3>";
        html+="</body>";
        html+="</html>";
        out.write(html);
		
		
		
		
	}

	@Override
	protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
		// TODO Auto-generated method stub
		
	}
	
	
	
}
